class User {

		var age: Int = 0

        get() {

		return field + 2;

        }

        set(value) {

		if (value > 0) {

		field = value;

		}

        }

		fun printInfo() {

        println("The user is $age years old.");

		}

		}


		fun main(args: Array<String>) {

		// client code

		val obj = User();

		obj.age = -12;

		obj.printInfo();

		obj.age = 11;

		obj.printInfo();

		println("Accessing age gives the value ${obj.age}");

		}

